    constructor(type) {
        this.type = type;
        this.title = type;
    }

    cssTd = "";
    colSpan = 1;
    cssLink = "d-flex align-items-center text-dark";
    cssIcon = "fa fa-tags";
    cssType = "d-inline-block pl-item hover-t-decoration";
}

