const titleNovel = novel => {
    const temp = (novel.before !== "") ? (novel.before + " " + novel.name) : novel.name;

    novel.title = (novel.after !== "") ? (temp + " " + novel.after) : temp;
}

const NameAndTitle = {titleNovel};

export default NameAndTitle;
