import { renderRows, TitleSection, RenderTypeNovels, ContainerNovels, More } from './components.js';
import { NovelsList } from './section.js';
import dataDefault from './data.js';
import images from './imageFiles.js';

const Content = () => {
    return <div className="content">
        <div className="max-width-main mx-auto">
            <div className="d-flex flex-wrap mh-content-negative">
                <Main />
                <Aside />
            </div>
        </div>
    </div>
}

const Main = () => {
    return <main className="max-860-w-full col-8">
        <div className="m-content">
            <SuggestedNovels />
            <SelectedTranslationNovels sectionName={nameOfSelectedTranslation} novels={dataDefault.SelectedTranslationNovels} />
            <UpdatedNovels />
            <FullNovels />
            <ShortNovels />
        </div>
    </main>
}

const SuggestedNovels = () => {
    const types = ["Ngôn Tình", "Truyện Teen", "Tiên Hiệp", "Kiếm Hiệp", "Xuyên Không"];
    const suggestedNovels = [{carousel: "carousel-item active", 
        novels:[{title: "[Thập Niên 70] Nàng Dâu May Mắn", novel: "[Thập Niên 70] <cite>Nàng Dâu May Mắn</cite>", chapter: "Full"},
        {title: "[Thập Niên 70] Nữ Thanh Niên Trí Thức Dũng Cảm", novel: "[Thập Niên 70] <cite>Nữ Thanh Niên Trí Thức Dũng Cảm</cite>", chapter: "Full"},
        {title: "Mỹ Nhân Nõn Nà Ở Năm 80 [Xuyên Thư]", novel: "<cite>Mỹ Nhân Nõn Nà Ở Năm 80</cite> [Xuyên Thư]", chapter: "Full"}]},
        {carousel: "carousel-item", 
        novels:[{title: "[Thập Niên 80] Mỹ Nhân Như Mật", novel: "[Thập Niên 80] <cite>Mỹ Nhân Như Mật</cite>", chapter: "Full"},
        {title: "Vạn Cổ Đệ Nhất Con Rể", novel: "<cite>Vạn Cổ Đệ Nhất Con Rể</cite>", chapter: "Chương 907"},
        {title: "Cửu Tinh Bá Thể Quyết", novel: "Cửu Tinh Bá Thể Quyết", chapter: "Chương 7330"}]}
    ];

    let Count = 0;

    for (let i = 0; i < suggestedNovels.length; i++)
    {
        let novels = suggestedNovels[i].novels;

        for (let i2 = 0; i2 < novels.length; i2++)
        {
            novels[i2].image = images.suggestedNovels[Count++];
        }
    }

    return <section className="bg-white">
        <TitleSection title="TRUYỆN ĐỀ CỬ" />
        <RenderTypeNovels types={types} />
        
    <div id="carouselExampleControls" className="carousel slide" data-bs-ride="carousel">
        <div className="carousel-inner">
            {suggestedNovels.map(renderGridSuggestedNovels)}
        </div>

        <button className="carousel-control-prev size-angle-icon" type="button" data-bs-target="#carouselExampleControls" data-bs-slide="prev">
            <i className="fa fa-angle-left size-icon"></i>
            <span className="visually-hidden">Previous</span>
        </button>

        <button className="carousel-control-next" type="button" data-bs-target="#carouselExampleControls" data-bs-slide="next">
            <i className="fa fa-angle-right size-icon"></i>
            <span className="visually-hidden">Next</span>
        </button>
    </div>
</section>
}

const renderGridSuggestedNovels = (SuggestedNovels) => {
    return <div className={SuggestedNovels.carousel}>
        <div className="d-grid grid-novel">{SuggestedNovels.novels.map(renderSuggestedNovels)}</div>
    </div>
}

const renderSuggestedNovels = (data) => {
    return <div className="position-relative">
        <img className="d-block" src={data.image} alt="" title={data.title} />
        <div className="position-absolute start-0 end-0 bottom-0 bg-black-opacity p-above">
            <a className="d-block text-white text-center hover-t-decoration" href="" title={data.title}>{data.novel}</a>
            <p className="text-white text-center">{data.chapter}</p>
        </div>
    </div>
}

const nameOfSelectedTranslation = "TRUYỆN DỊCH CHỌN LỌC";

// const translatedNovels = [{title: "Bắt Đầu Làm Thần Hào Từ Sau Khi Thi Đại Học", before: "", 
//     novel: "Bắt Đầu Làm Thần Hào Từ Sau Khi Thi Đại Học", after: "", chapter: "Chương 3565"},
//     {title: "Đan Hoàng Võ Đế", before: "", novel: "Đan Hoàng Võ Đế", after: "", chapter: "Chương 3564"},
//     {title: "Thì Ra Ta Là Tuyệt Thế Võ Thần", before: "", novel: "Thì Ra Ta Là Tuyệt Thế Võ Thần", after: "", chapter: "Chương 1312"},
//     {title: "Trở lại Thập Niên 70: Mang Theo Thành Thị Làm giàu", before: "Trở lại Thập Niên 70: ", 
//     novel: "Mang Theo Thành Thị Làm giàu", after: "", chapter: "Full"},
//     {title: "Người Đọc Sách Đại Ngụy", before: "", novel: "Người Đọc Sách Đại Ngụy", after: "", chapter: "Chương 3565"},
//     {title: "Bất Phụ Vinh Quang, Bất Phụ Em", before: "", novel: "Bất Phụ Vinh Quang, Bất Phụ Em", after: "", chapter: "Chương 553"},
//     {title: "[Thập Niên 60] Gây Dựng Lại Gia Đình", before: "[Thập Niên 60] ", novel: "Gây Dựng Lại Gia Đình", after: "", 
//     chapter: "Chương 163"},
//     {title: "[Thập Niên 70] Kiều Tức Phụ", before: "[Thập Niên 70] ", novel: "Kiều Tức Phụ", after: "", chapter: "Chương 246"}];

// for (let i = 0; i < translatedNovels.length; i++)
// {
//     translatedNovels[i].image = images.translatedNovels[i];
// }

const SelectedTranslationNovels = (props) => {
    const sectionName = props.sectionName;

    return <section className="bg-white">
        <TitleSection title={sectionName} />
        <ContainerNovels novels={props.novels} />
        <More title={sectionName} />
    </section>
}

const UpdatedNovels = () => {
    const sectionName = "TRUYỆN MỚI CẬP NHẬT";
    const types = ["Ngôn Tình", "Truyện Teen", "Tiên Hiệp", "Kiếm Hiệp", "Xuyên Không"];

    return <section className="bg-white">
        <TitleSection title={sectionName} />
        <RenderTypeNovels types={types} />
        <RenderUpdatedNovels novels={dataDefault.UpdatedNovels} />
        <More title={sectionName} />
    </section>
}

const RenderUpdatedNovels = (props) => {
    return <table class="table table-hover table-update-novel align-middle">{props.novels.map(renderUpdatedNovel)}</table>
}

const renderUpdatedNovel = (novel) => {
    return <tr>
        <td class="d-flex align-items-center">
            <a class="d-inline-block text-dark hover-t-decoration title-novel-update" href="" title={novel.title}>
                {novel.before}<cite>{novel.name}</cite>{novel.after}
            </a>
            {(novel.note !== "") && <p class="note text-hot size-text-secondary">{novel.note}</p>}
        </td>
        <td class="text-secondary td-secondary size-text-secondary">{novel.chapter}</td>
        <td class="text-secondary td-secondary size-text-secondary">{novel.time}</td>
    </tr>
}

const FullNovels = () => {
    const sectionName = "TRUYỆN FULL";
    const types = ["Ngôn Tình", "Truyện Teen", "Tiên Hiệp", "Kiếm Hiệp", "Xuyên Không"];
    
    return <section className="bg-white">
        <TitleSection title={sectionName} />
        <RenderTypeNovels types={types} />
        <ContainerNovels novels={dataDefault.FullNovels} />
        <More title={sectionName} />
    </section>
}

const ShortNovels = () => {
    return <section className="bg-white">
        <TitleSection title="TRUYỆN NGẮN" />
        <div className="d-flex flex-wrap col-short-novel row-cols-2 pb-shorts-novel">
            <LeftOfShortNovels novel={dataDefault.ShortNovels.LeftOfShortNovels} />
            <RightOfShortNovels novels={dataDefault.ShortNovels.RightOfShortNovels} />
        </div>
    </section>
}

const LeftOfShortNovels = (props) => {
    const novel = props.novel;

    return <div className="position-relative">
        <img src={novel.image} alt="" title={novel.title} />
        <a className="d-inline-block text-white hover-t-decoration position-absolute start-0 end-0 bottom-0 p-above" title={novel.title}>
            {novel.title}
        </a>
    </div>
}

const RightOfShortNovels = (props) => {
    return <div className="right-novel">{props.novels.map(renderShortNovel)}</div>
}

const renderShortNovel = (novel) => {
    return <div className="d-flex pb-short-novel">
        <img src={novel.image} alt="" title={novel.title} />
        <a className="text-dark hover-t-decoration" href="" title={novel.title}>{novel.title}</a>
    </div>
}

const dataTopGoodNovels = [{rank: "01", title: "Ngạo Thế Đan Thần", view: "287,331,252"},
    {rank: "02", title: "Kiếm Ngạo Độc Tôn", view: "85,756,094"},{rank: "03", title: "Linh Vũ Thiên Hạ", view: "81,893,396"},
    {rank: "04", title: "Độc Y Thần Nữ: Phúc Hắc Lãnh Đế Cuồng Sủng Thê", view: "74,628,662"},
    {rank: "05", title: "Hôn Trộm 55 Lần", view: "55,185,032"},{rank: "05", title: "Ép Yêu 100 Ngày", view: "53,212,018"},
    {rank: "07", title: "Tuyệt Sắc Đan Dược Sư: Quỷ Vương Yêu Phi", view: "52,128,456"},
    {rank: "08", title: "Hợp Đồng Hôn Nhân 100 Ngày", view: "47,532,135"},
    {rank: "09", title: "Ngạo Thế Cửu Trọng Thiên", view: "41,404,354"},
    {rank: "10", title: "Cô Vợ Ngọt Ngào Có Chút Bất Lương", view: "40,124,578"}
];

const dataNewUpdateNovels = [{rank: "01", title: "Game Khuya", view: "0"},{rank: "02", title: "Nhặt Được Sức Mạnh Của Thần", view: "0"},
    {rank: "03", title: "Xuyên Không Mất Rồi!", view: "0"},{rank: "04", title: "Tình Sói Chi Dao", view: "50"},
    {rank: "05", title: "Siêu Năng Lực Gia Tại Thời Đại Vua Hải Tặc", view: "50"},{rank: "06", title: "Lao Sơn Đạo Sĩ", view: "0"},
    {rank: "07", title: "Thầy Giáo Đã Kết Hôn Dụ Dỗ Tôi", view: "150"},{rank: "08", title: "Những Năm Ấy Tôi Làm Chó Liếm", view: "50"},
    {rank: "09", title: "Trở Thành Đồ Chơi Trong Game Otome", view: "0"},{rank: "10", title: "Tận Thế Lưu Vong", view: "0"}
];

for (let i = 0; i < 10; i++)
{
    dataTopGoodNovels[i].cssRank = "rank text-under-r-3";
    dataNewUpdateNovels[i].cssRank = "rank text-under-r-3";
}

dataTopGoodNovels[0].cssRank = "rank text-r-1";
dataTopGoodNovels[1].cssRank = "rank text-r-2";
dataTopGoodNovels[2].cssRank = "rank text-r-3";
dataNewUpdateNovels[0].cssRank = "rank text-r-1";
dataNewUpdateNovels[1].cssRank = "rank text-r-2";
dataNewUpdateNovels[2].cssRank = "rank text-r-3";

const Aside = () => {
    return <aside class="max-860-w-full col-4">
        <div class="m-content">
            <TypeNovels />
            <ReviewNovels />
            <NovelsList sectionName="TOP TRUYỆN HAY" novels={dataTopGoodNovels} />
            <NovelsList sectionName="TRUYỆN MỚI ĐĂNG" novels={dataNewUpdateNovels} />
        </div>
    </aside>
}

const TypeNovels = () => {
    const rows = [[{title: "Truyện Tiên Hiệp", type: "Tiên Hiệp"}, {title: "Kiếm Hiệp", type: "Kiếm Hiệp"}],
        [{title: "Truyện Teen", type: "Truyện Teen"}, {title: "Ngôn Tình", type: "Ngôn Tình"}],
        [{title: "Đoản Văn", type: "Đoản Văn"}, {title: "Đông Phương", type: "Đông Phương"}],
        [{title: "Gia Đấu", type: "Gia Đấu"}, {title: "Nữ Cường", type: "Nữ Cường"}],
        [{title: "Cung Đấu", type: "Cung Đấu"}, {title: "Truyện Sủng", type: "Truyện Sủng"}],
