const TitleSection = (props) => {
    return <h2><span className="hover-t-decoration" title={props.title}>{props.title}</span> <i class="fa fa-angle-right"></i></h2>
}

const RenderTypeNovels = (props) => {
    return <div className="d-flex flex-wrap container-type-novel">{props.types.map(renderTypeNovel)}</div>
}

const renderTypeNovel = (type) => {
    return <a className="d-block text-white hover-t-decoration" href="">{type}</a>
}

const ContainerNovels = (props) => {
    return <div className="row cols-novel row-cols-4 m-0">{props.novels.map(NovelInGrid)}</div>
}

const NovelInGrid = (novel) => {
    return <div className="container-novel">
        <div><img src={novel.image} alt="" title={novel.title} /></div>
        <div className="container-infor-novel">
            <a className="d-block text-dark hover-t-decoration" href="" title={novel.title}>
                {novel.before}<cite>{novel.novel}</cite>{novel.after}
            </a>
            <p className="text-secondary">{novel.chapter}</p>
        </div>
    </div>
}

const More = (props) => {
    return <div className="d-flex">
        <a className="d-flex align-items-center justify-content-center text-white mx-auto pill" href="" title={props.title}>
            <p className="d-inline-block hover-t-decoration">XEM THÃŠM</p>
            <i className="d-inline-block fa fa-angle-down"></i>
        </a>
    </div>
}

export {TitleSection, RenderTypeNovels, ContainerNovels, More};
