export let carouselsData;

const suggestedNovels_ = [{before: "[Thập Niên 70]", name: "Nàng Dâu May Mắn", after: "", chapter: "Full"}, 
{before: "[Thập Niên 70]", name: "Nữ Thanh Niên Trí Thức Dũng Cảm", after: "", chapter: "Full"}, 
{before: "", name: "Mỹ Nhân Nõn Nà Ở Năm 80", after: "[Xuyên Thư]", chapter: "Full"}, 
{before: "[Thập Niên 80]", name: "Mỹ Nhân Như Mật", after: "", chapter: "Full"}, 
{before: "", name: "Vạn Cổ Đệ Nhất Con Rể", after: "", chapter: "Chương 907"}, 
{before: "", name: "Cửu Tinh Bá Thể Quyết", after: "", chapter: "Chương 7330"}];

suggestedNovels_.map((novel, i) => novel.image = require(`./images/truyenDeCu${i + 1}.jpg`));



const setsuggestedNovelsScope = () => {
    let sectionData = suggestedNovels_;
    let length = Math.ceil(sectionData.length/3);
    carouselsData = new Array(length);

    for (let i = 0; i < length; i++) {
        carouselsData[i] = {cssCarousel: "carousel-item", novels: []};
    }

    length = sectionData.length;

    for (let i = 0; i < length; i++) {
        titleNovel_(sectionData[i]);
        carouselsData[Math.floor(i/3)].novels.push(sectionData[i]);
    }

    carouselsData[0].cssCarousel = "carousel-item active";
}

const titleNovel_ = novel => {
    novel.title = (novel.before !== "") ? (novel.before + " " + novel.name) : novel.name;
    novel.title = (novel.after !== "") ? (novel.title + " " + novel.after) : novel.title;
}

setsuggestedNovelsScope();

export const SelectedTranslationNovels_ = [{before: "", name: "Bắt Đầu Làm Thần Hào Từ Sau Khi Thi Đại Học", after: "", chapter: "Chương 3565"},
