import { useState, useEffect } from "react";

const useUpdate = (name, propsFunction, other) => {
    let [updatedTime, setUpdatedTime] = useState(window.lastUpdatedTime[name]);
    useEffect(() => {
        window.setLastUpdatedTime[name] = setUpdatedTime;
    }, []);
    useEffect(() => {
        propsFunction(name, other);console.log(updatedTime);
    }, [updatedTime]);
}

export default useUpdate;
