} else {
    window.tempData = {};
}

if (!(window.lastUpdatedTime === undefined)) {
    console.log("lastUpdatedTime has been defined in window object");
} else {
    window.lastUpdatedTime = {};
}

if (!(window.setLastUpdatedTime === undefined)) {
