import Components from './components.js';
import NameAndTitle from './nameAndTitle.js';
//---------------------------
const ShowNovels = props => {
    const sectionName = props.sectionName;
    const types = ["Ngôn Tình", "Truyện Teen", "Tiên Hiệp", "Kiếm Hiệp", "Xuyên Không"];

    return <section className="bg-white">
        <Components.TitleSection title={sectionName} />
        {(props.haveTypeNovels === 1) && <Components.RenderTypeNovels types={types} />}
        <ContainerNovels VerticalDecriptionHOC={props.VerticalDecriptionHOC} />
        <Components.More title={sectionName} />
    </section>
}

const ContainerNovels = Props => {
    return <div className="row cols-novel row-cols-4 m-0"><Props.VerticalDecriptionHOC /></div>
}

const VerticalDecriptionHOC = props => {
    return props.novels.map(VerticalDecription);
}

const VerticalDecription = novel => {
    Components.titleNovel(novel);

    return <div key={novel.name} className="container-novel">
        <div><img src={novel.image} alt="" title={novel.title} /></div>
        <div className="container-infor-novel">
            <a className="d-block text-dark hover-t-decoration" title={novel.title} href="/#">
                <Components.NameNovel novel={novel} />
            </a>
            <p className="text-secondary">{novel.chapter}</p>
        </div>
    </div>
}
//---------------------------
const NovelsList = Props => {
    const sectionName = Props.sectionName;

    return <section className="bg-white">
        <Components.TitleSection title={sectionName} />
        <Props.RenderNovelsList />
        <Components.More title={sectionName} />
    </section>
}
//---------------------------
const Sections = {ShowNovels, VerticalDecriptionHOC, NovelsList};

export default Sections;
