import images from './imageFiles.js';
import './App.css';

function Header() {
    return <>
        <header className="header">
            <nav className="max-width-main mx-auto">
                <div className="d-flex flex-wrap mx-nav-negative">
                    <ReadNovelOnline />
                    <ListOfTypeNovels />
                    <Arrange />
                    <UpLoad />
                    <Search />
                    <LogIn />
                </div>
            </nav>
        </header>
    </>
}

function ReadNovelOnline() {
    return <>
        <div className="d-flex align-items-center p-item-main title-main-hover" title="doc truyen online">
            <img className="d-block img" src={images.logo} alt="" />
            <h1><a className="d-inline-block color-main-list" href="">Doc Tryen Online</a></h1>
        </div>
    </>
}

function ListOfTypeNovels() {
    const rows = [[{title: "Truyện Tiên Hiệp", type: "Tiên Hiệp"}, {title: "Truyện Kiếm Hiệp", type: "Kiếm Hiệp"}],
        [{title: "Truyện Ngôn Tình", type: "Ngôn Tình"}, {title: "Truyện Teen", type: "Truyện Teen"}],
        [{title: "Truyện Đô Thị", type: "Đô Thị"}, {title: "Truyện Quân Sự", type: "Quân Sự"}],
        [{title: "Truyện Lịch Sử", type: "Lịch Sử"}, {title: "Truyện Xuyên Không", type: "Xuyên Không"}],
        [{title: "Truyện Ma", type: "Truyện Ma"}, {title: "Truyện Trinh Thám", type: "Trinh Thám"}],
        [{title: "Truyện Huyền Huyễn", type: "Huyền Huyễn"}, {title: "Truyện Khoa Huyễn", type: "Khoa Huyễn"}],
        [{title: "Truyện Dị Giới", type: "Dị Giới"}, {title: "Truyện Võng Du", type: "Võng Du"}],
        [{title: "Truyện Ngắn", type: "Truyện Ngắn"}, {title: "Truyện Cười", type: "Truyện Cười"}],
        [{title: "Truyện Tiểu Thuyết", type: "Tiểu Thuyết"}, {title: "Review Truyện", type: "Review"}]];

    rows.map((row) => {
        row.map(updateDataColumn);

        return row;
    });

    function updateDataColumn(column) {
        column.cssLink = "d-flex align-items-center item";
        column.cssIcon = "d-block fa fa-tags";
        column.colspan = 1;

        return column;
    }

    return <div className="max-460-none d-flex-edit align-items-center p-item-main position-relative hover-show">
        <i className="d-block fa fa-tags color-main-list"></i>
        <div><p className="d-inline-block pl-item color-main-list item-main-hover">Thể loại</p></div>
        <table className="position-absolute top-100 start-0 table table-bordered bg-white w-table">
            {rows.map(renderRows)}
        </table>
    </div>
}

function renderRows(row) {
    return <ColumnsPerRow column={row} />
}

function ColumnsPerRow(props) {
    return <tr>{props.column.map((data) => {return <Item data={data} />})}</tr>
}

function Item(props) {
    const data = props.data;

    return <td className="item-sub-hover" colspan={data.colspan} title={data.title}>
        <a className={data.cssLink} href="">
            <i className={data.cssIcon}></i>
            <div><p className="d-inline-block pl-item">{data.type}</p></div>
        </a>
    </td>
}

function Arrange() {
    const rows = [[{cssIcon: "d-block fa fa-diamond", title: "Truyện Dịch", type: "Truyện Dịch"}, 
        {cssIcon: "d-block fa fa-refresh", title: "Truyện Mới Cập Nhật", type: "Mới Cập Nhật"}],
        [{cssIcon: "d-block fa fa-cloud-upload", title: "Truyện Mới Đăng", type: "Mới Đăng"}, 
        {cssIcon: "d-block fa fa-eye", title: "Truyện Đọc Nhiều", type: "Xem Nhiều"}],
        [{cssIcon: "d-block fa fa-thumbs-o-up", title: "Truyện Được Yêu Thích", type: "Yêu Thích"}, 
        {cssIcon: "d-block fa fa-signal", title: "Truyện Full", type: "Truyện Full"}],
        [{cssIcon: "d-block fa fa-star", title: "Truyện Sáng Tác", type: "Thành Viên Sáng Tác"}]];

    rows.map((row) => {
        row.map(updateDataColumn);

        return row;
    });
    
    function updateDataColumn(column) {
        column.cssLink = "d-flex align-items-center item";
        column.colspan = 1;

        return column;
    }

    rows[0][0].cssLink = "d-flex align-items-center text-hot";
    rows[3][0].colspan = 2;
    rows[3][0].cssLink = "d-flex align-items-center justify-content-center text-hot";

    return <>
        <div className="max-460-none d-flex-edit align-items-center p-item-main position-relative hover-show">
            <i className="d-block fa fa-list color-main-list"></i>
            <div><p className="d-inline-block pl-item color-main-list item-main-hover">Sắp xếp</p></div>

            <table className="position-absolute top-100 start-0 table table-bordered bg-white w-table">
                {rows.map(renderRows)}

                <tr>
                    <td className="item-sub-hover" colspan="2" title="Truyện Sáng Tác">
                        <a className="d-flex align-items-center justify-content-center text-hot" href="">
                            <i className="d-block fa fa-star"></i>
                            <div><p className="d-inline-block pl-item">Thành Viên Sáng Tác</p></div>
                        </a>
                    </td>
                </tr>
            </table>
        </div>
    </>
}

function UpLoad() {
    return <div className="d-flex align-items-center p-item-main bg-upload" title="Đăng truyện">
        <i className="d-block fa fa-cloud-upload color-main-list"></i>
        <div><p className="d-inline-block pl-item color-main-list item-main-hover">Đăng truyện</p></div>
    </div>
}

function Search() {
    return <form className="d-flex  p-item-main col" action="">
        <input className="d-block col" type="text" placeholder="Tìm truyện, tác giả..." />
        <button className="d-block ml-sub-item" type="button"><i className="d-block fa fa-search"></i></button>
    </form>
}

function LogIn() {
    return <div className="max-778-none d-flex-edit align-items-center p-item-main log-in-hover" title="Đăng nhập">
        <i className="d-block fa fa-user color-main-list"></i>
        <div><p className="d-inline-block pl-item color-main-list item-main-hover">Thành viên</p></div>
    </div>
}

export default Header;
