import { TitleSection, More } from './components.js';

const NovelsList = (props) => {
    const sectionName = props.sectionName;
    const novels = props.novels;

    rank(novels);
    classRank(novels);

    return <section class="bg-white">
        <TitleSection title={sectionName} />
        <RenderNovelsList novels={novels} />
        <More title={sectionName} />
    </section>
}

const rank = (novels) => {
    
    let count = novels.length.toString().length;
    

    // while (length > 0) {
    //     count++;
    //     length = Math.floor(length/10);
    // }
    let length = novels.length;
    length = novels.length;

    for (let i = 0; i < length; i++)
    {
        novels[i].rank = (i + 1) + "";
        novels[i].rank = novels[i].rank.padStart(count, "0");
    }
}

const classRank = (novels) => {
    const length = novels.length;
    
    (length > 0) && (novels[0].cssRank = "rank text-r-1");
    (length > 1) && (novels[1].cssRank = "rank text-r-2");
    (length > 2) && (novels[2].cssRank = "rank text-r-3");

    for (let i = 3; i < length; i++)
    {
        novels[i].cssRank = "rank text-under-r-3";
    }
}

const RenderNovelsList = (props) => {
    return <table class="table top-list">{props.novels.map(renderRankNovel)}</table>
}

const renderRankNovel = (novel) => {
    return <tr>
        <td class={novel.cssRank}>{novel.rank}</td>
        <td>
            <a class="d-inline-block text-dark hover-t-decoration" title={novel.title}>{novel.title}</a>
            <div class="d-flex align-items-center view text-secondary">
                <i class="fa fa-eye"></i>
                <p>{novel.view}</p>
            </div>
        </td>
    </tr>
}

export {NovelsList};
