import Components from './Components.js';
import Sections from './Sections.js';
import { connect } from "react-redux";
import ProcessData from './MapState.js';
import ConnectTemplate from './ConnectTemplate.js';

const Content = () => {
    return <div className="content">
        <div className="max-width-main mx-auto">
            <div className="d-flex flex-wrap mh-content-negative">
                <Main />
                <Aside />
            </div>
        </div>
    </div>
}

const Main = () => {
    return <main className="max-860-w-full col-8">
        <div className="m-content">
            <SuggestedNovels />
            <Sections.ShowNovels sectionName="TRUYỆN DỊCH CHỌN LỌC" haveTypeNovels={0} VerticalDecriptionHOC={SelectedTranslationNovelsHOC} />
            <UpdatedNovels />
            <Sections.ShowNovels sectionName="TRUYỆN FULL" haveTypeNovels={1} VerticalDecriptionHOC={FullNovelsHOC} />
            <ShortNovels />
        </div>
    </main>
}

const SuggestedNovels = () => {
    const types = ["Ngôn Tình", "Truyện Teen", "Tiên Hiệp", "Kiếm Hiệp", "Xuyên Không"];

    return <section className="bg-white">
        <Components.TitleSection title="TRUYỆN ĐỀ CỬ" />
        <Components.RenderTypeNovels types={types} />
        <Carousels />
    </section>
}

const Carousels = () => {
    return <div id="carouselExampleControls" className="carousel slide" data-bs-ride="carousel">
        <div className="carousel-inner"><CarouselsHOC /></div>
        <button className="carousel-control-prev size-angle-icon" type="button" data-bs-target="#carouselExampleControls" data-bs-slide="prev">
            <i className="fa fa-angle-left size-icon"></i>
            <span className="visually-hidden">Previous</span>
        </button>
        <button className="carousel-control-next" type="button" data-bs-target="#carouselExampleControls" data-bs-slide="next">
            <i className="fa fa-angle-right size-icon"></i>
            <span className="visually-hidden">Next</span>
        </button>
    </div>
}

const CarouselsHOC = connect(ProcessData.mapSuggestedNovels)(ConnectTemplate.CarouselsTemplate);

const SelectedTranslationNovelsHOC = connect(ProcessData.mapSelectedTranslationNovels)(ConnectTemplate.VerticalDecriptionTemplate);

const UpdatedNovels = () => {
    const sectionName = "TRUYỆN MỚI CẬP NHẬT";
    const types = ["Ngôn Tình", "Truyện Teen", "Tiên Hiệp", "Kiếm Hiệp", "Xuyên Không"];

    return <section className="bg-white">
        <Components.TitleSection title={sectionName} />
        <Components.RenderTypeNovels types={types} />
        <RenderUpdatedNovels />
        <Components.More title={sectionName} />
    </section>
}

const RenderUpdatedNovels = () => {return <table className="table table-hover table-update-novel align-middle"><UpdatedNovelsHOC /></table>}
const UpdatedNovelsHOC = connect(ProcessData.mapUpdatedNovels)(props => {return props.novels.map(renderUpdatedNovel);});

const renderUpdatedNovel = novel => {
    Components.titleNovel(novel);

    return <tr key={novel.name}>
        <td className="d-flex align-items-center">
            <a className="d-inline-block text-dark hover-t-decoration title-novel-update" title={novel.title} href="/#">
                <Components.NameNovel novel={novel} />
            </a>
            {(novel.note !== "") && <p className="note text-hot size-text-secondary">{novel.note}</p>}
        </td>
        <td className="text-secondary td-secondary size-text-secondary">{novel.chapter}</td>
        <td className="text-secondary td-secondary size-text-secondary">{novel.time}</td>
    </tr>
}

const FullNovelsHOC = connect(ProcessData.mapFullNovels)(ConnectTemplate.VerticalDecriptionTemplate);

const ShortNovels = () => {
    return <section className="bg-white">
        <Components.TitleSection title="TRUYỆN NGẮN" />
        <div className="d-flex flex-wrap col-short-novel row-cols-2 pb-shorts-novel"><ShortNovelsHOC /></div>
    </section>
}

const ShortNovelsHOC = connect(ProcessData.mapShortNovels)(({novels}) => {
    return <>
        <LeftOfShortNovels novel={novels.LeftOfShortNovels} />
        <RightOfShortNovels novels={novels.RightOfShortNovels} />
    </>
});

const LeftOfShortNovels = ({novel}) => {
    return <div className="position-relative">
        <img src={novel.image} alt="" title={novel.title} />
        <a className="d-inline-block text-white hover-t-decoration position-absolute start-0 end-0 bottom-0 p-above" title={novel.title} href="/#">
            {novel.title}
        </a>
    </div>
}

const RightOfShortNovels = ({novels}) => {
    novels.map(novel => novel.cssContainer = "d-flex pb-short-novel");

    return <div className="right-novel">{novels.map(Components.HorizontalDecription)}</div>
}

const Aside = () => {
    return <aside className="max-860-w-full col-4">
        <div className="m-content">
            <TypeNovels />
            <ReviewNovels />
            <Sections.NovelsList sectionName="TOP TRUYỆN HAY" RenderNovelsList={TopGoodNovelsHOC} />
            <Sections.NovelsList sectionName="TRUYỆN MỚI ĐĂNG" RenderNovelsList={NewUpdateNovelsHOC} />
        </div>
    </aside>
}

const TypeNovels = () => {
    const rows = [[{type: "Tiên Hiệp"}, {type: "Kiếm Hiệp"}], [{type: "Truyện Teen"}, {type: "Ngôn Tình"}], 
        [{type: "Đoản Văn"}, {type: "Đông Phương"}], [{type: "Gia Đấu"}, {type: "Nữ Cường"}], [{type: "Cung Đấu"}, {type: "Truyện Sủng"}],
        [{type: "Truyện Ngược"}, {type: "Linh Dị"}], [{type: "Thám Hiểm"}, {type: "Bách Hợp"}], [{type: "Hài Hước"}, {type: "Hiện Đại"}],
        [{type: "Việt Nam"}, {type: "Light Novel"}], [{type: "Nữ Phụ"}, {type: "Phương Tây"}], [{type: "Mạt Thế"}, {type: "Cổ Đại"}],
        [{type: "Điền Văn"}, {type: "Đồng Nhân"}], [{type: "Trọng Sinh"}, {type: "Dị Năng"}], [{type: "Huyền Huyễn"}, {type: "Dị Giới"}],
        [{type: "Võng Du"}, {type: "Quân Sự"}], [{type: "Khoa Học"}, {type: "Lịch Sử"}], [{type: "Truyện Khác"}, {type: "Đô Thị"}],
        [{type: "Khoa Huyễn"}, {type: "Xuyên Không"}], [{type: "Truyện Ma"}, {type: "Xuyên Nhanh"}], 
        [{type: "Quan Trường"}, {type: "Đam Mỹ"}], [{type: "Hệ Thống"}, {type: "Tiểu Thuyết"}], 
        [{type: "Truyện Cười"}, {type: "Truyện Ngắn"}], [{type: "Truyện Trinh Thám"}, {type: "Truyện Sắc"}]];

    rows.map(row => row.map(propsColumnOfTypeNovels));

    for (let i = 0; i < 2; i++)
    {
        for (let i2 = 0; i2 < 2; i2++)
        {
            rows[i][i2].cssLink = "d-flex align-items-center text-hot";
            rows[i][i2].cssIcon = "fa fa-diamond";
        }
    }

    return <section className="bg-white type-novel">
        <Components.TitleSection title="THỂ LOẠI TRUYỆN" />
        <Components.TableOfList cssTable="table bg-white w-100" rows={rows} />
    </section>
}

const propsColumnOfTypeNovels = column => {
    column.cssTd = "";
    column.colSpan = 1;
    column.title = column.type;
    column.cssLink = "d-flex align-items-center text-dark";
    column.cssIcon = "fa fa-tags";
    column.cssType = "d-inline-block pl-item hover-t-decoration";
}

const ReviewNovels = () => {
    const sectionName = "REVIEW TRUYỆN";

    return <section className="bg-white review-novel">
        <Components.TitleSection title={sectionName} />
        <ReviewNovelsHOC />
        <Components.More title={sectionName} />
    </section>
}

const ReviewNovelsHOC = connect(ProcessData.mapReviewNovels)(({novels}) => {
    novels.map(novel => novel.cssContainer = "d-flex p-short-novel");

    return novels.map(Components.HorizontalDecription);
});

const TopGoodNovelsHOC = connect(ProcessData.mapTopGoodNovels)(Components.RenderNovelsList);
const NewUpdateNovelsHOC = connect(ProcessData.mapNewUpdateNovels)(Components.RenderNovelsList);

export default Content;
