if (!(window.data === undefined)) {
    console.log("data has been defined in window object");
} else {
    window.data = {};
}

if (!(window.setLastUpdatedTime === undefined)) {
    console.log("setLastUpdatedTime has been defined in window object");
} else {
    window.setLastUpdatedTime = {
        suggestedNovels: "", selectedTranslationNovels: "", updatedNovels: "", fullNovels: "", leftOfShortNovel: "", rightOfShortNovels: "", 
        reviewNovels: "", topGoodNovels: "", newUpdateNovels: "", trendNovelsInMonth: ""
    };
}

const requestGetSynchronized = (request, url) => {
    request.open("GET", url, false);
    request.send();

    return request.responseText;
}

const propsSuggestedNovels = (suggestedNovels) => {
    let length = Math.ceil(suggestedNovels.length/3);
    let carousels = new Array(length);

    for (let i = 0; i < length; i++) {
        carousels[i] = {cssCarousel: "carousel-item", novels: []};
    }

    length = suggestedNovels.length;

    for (let i = 0; i < length; i++) {
        suggestedNovels[i].image = require("./images/" + suggestedNovels[i].image);
        titleNovel_(suggestedNovels[i]);
        carousels[Math.floor(i/3)].novels.push(suggestedNovels[i]);
    }

    carousels[0].cssCarousel = "carousel-item active";

    window.data.suggestedNovels = carousels;
}

const propsShowNovels = (data, name) => {
    for (let i = 0; i < data[name].length; i++) {
        data[name][i].image = require("./images/" + data[name][i].image);
    }

    data[name].forEach(titleNovel_);

    window.data[name] = data[name];
}

const propsUpdatedNovels = updatedNovels => {
    updatedNovels.forEach(titleNovel_);

    window.data.updatedNovels = updatedNovels;
}


const titleNovel_ = novel => {
    novel.title = (novel.before !== "") ? (novel.before + " " + novel.name) : novel.name;
    novel.title = (novel.after !== "") ? (novel.title + " " + novel.after) : novel.title;
}






let request = new XMLHttpRequest();
let text = requestGetSynchronized(request, "data.json");//data.json is located in public folder
let json = JSON.parse(text);
let data = json.data;

propsSuggestedNovels(data.suggestedNovels);
propsShowNovels(data, "selectedTranslationNovels");
propsUpdatedNovels(data.updatedNovels);
propsShowNovels(data, "fullNovels");
