import images from './imageFiles.js';

const Footer = () => {
    const novels = ["mẹ 17 tuổi con trai thiên tài cha phúc hắc", "thần ma hệ thống", "truyện trinh thám", "năm tháng vội vã", 
        "truyen sac dep kho cuong", "vô tận đan điền", "ngôn tình cung đấu", "bên nhau trọn đời", "huyền của ôn noãn", 
        "bach luyen thanh tien"
    ];
    const text1 = ["Website ",{title: "đọc truyện online", marker: "đọc truyện"}," online chất lượng hàng đầu Việt Nam, với nhiều ",
    {title: "Truyện Tiên Hiệp", marker: "truyện tiên hiệp"},", ",{title: "Truyện Kiếm Hiệp", marker: "truyện kiếm hiệp"},", ",
    {title: "Truyện Ngôn Tình", marker: "truyện ngôn tình"},", ",{title: "Truyện Teen", marker: "truyện teen"},", ",
    {title: "Truyện Đô Thị", marker: "truyện đô thị"}," được tác giả và dịch giả chọn lọc và đăng tải. Truy cập website nghĩa là bạn đã đồng"
    + " ý với các quy định và điều khoản của chúng tôi. Vui lòng đọc kỹ các thông tin liên quan ở phía dưới."];
    const text2 = [{title: "Privacy Policy", marker: "Chính sách bảo mật"}," - ",{title: "Terms", marker: "Điều khoản sử dụng"}," - ",
    {title: "Content Rules", marker: "Quy định về nội dung"}," - ",{title: "DMCA Guidelines", marker: "Vấn đề bản quyền"}," - ",
    {title: "Privacy agreement", marker: "Thỏa thuận quyền riêng tư"}];

    return <footer class="footer">
    <div class="max-width-main mx-auto">
        <div class="bg-dark p-top-novel-month">
            <h2 class="text-white h2">Top truyện nổi bật của tháng</h2>
            {novels.map(renderNovel)}
        </div>

        <div class="d-flex flex-wrap pv-description">
            <div class="max-860-w-full col-8">
                <Paragraph text={text1} />
                <Paragraph text={text2} />
                {/* <p class="text-white"><HighLigh data={text2[0]} />&nbsp;-&nbsp;<HighLigh data={text2[1]} />&nbsp;-&nbsp;
                    <HighLigh data={text2[2]} />&nbsp;-&nbsp;<HighLigh data={text2[3]} />&nbsp;-&nbsp;<HighLigh data={text2[4]} />
                </p> */}
                <p class="text-white">Website hoạt động dưới <a class="text-link hover-t-decoration" href="">Giấy phép truy cập mở Creative 
                Commons Attributio 4.0 International License.</a></p>
                <div class="d-flex align-items-center">
                    <a class="pr-logo" href="" title="DMCA.com Protection Program"><img src={images.dmca} alt=""/></a>
                    <a href="" title="Creative Commons License"><img src={images.ccBy} alt=""/></a>
                </div>
            </div>
                
            <div class="max-860-w-full col-4">
                <p class="text-white text-center">Copy right 2021 <a class="text-link" href="">DTruyen.Com</a></p>
            </div>
        </div>
    </div>
    </footer>
}

const renderNovel = (novel) => {
    return <a class="d-inline-block top-novel hover-t-decoration" href="">{novel}</a>
}

const Paragraph = (props) => {
    return <p class="text-white">{props.text.map(renderParagraph)}</p>
}

const renderParagraph = (text) => {
    return (typeof text === "string") ? text : <HighLigh data={text} />;
}

const HighLigh = (props) => {
    const data = props.data;

    return <a class="text-link hover-t-decoration" href="" title={data.title}>{data.marker}</a>
}

export default Footer;
