import Components from './components.js';
//---------------------------
const ShowNovels = props => {
    const sectionName = props.sectionName;
    const types = ["Ngôn Tình", "Truyện Teen", "Tiên Hiệp", "Kiếm Hiệp", "Xuyên Không"];

    return <section className="bg-white">
        <Components.TitleSection title={sectionName} />
        {(props.haveTypeNovels === 1) && <Components.RenderTypeNovels types={types} />}
        <Components.ContainerNovels novels={props.novels} />
        <Components.More title={sectionName} />
    </section>
}
//---------------------------
const NovelsList = props => {
    const sectionName = props.sectionName;

    return <section className="bg-white">
        <Components.TitleSection title={sectionName} />
        <RenderNovelsList novels={props.novels} />
        <Components.More title={sectionName} />
    </section>
}

const RenderNovelsList = props => {
    const novels = props.novels;

    rank(novels);
    classRank(novels);

    return <table className="table top-list">{novels.map(renderRankNovel)}</table>
}

const rank = novels => {
    let numberNovels = novels.length;
    let count = numberNovels.toString().length;

    for (let i = 0; i < numberNovels; i++)
    {
        novels[i].rank = (i + 1) + "";
        novels[i].rank = novels[i].rank.padStart(count, "0");
    }
}

const classRank = novels => {
    novels.map(novel => novel.cssRank = "rank text-under-r-3");

    const length = novels.length;

    (length > 0) && (novels[0].cssRank = "rank text-r-1");
    (length > 1) && (novels[1].cssRank = "rank text-r-2");
    (length > 2) && (novels[2].cssRank = "rank text-r-3");
}

const renderRankNovel = (novel) => {
    return <tr key={novel.title}>
        <td className={novel.cssRank}>{novel.rank}</td>
        <td>
            <a className="d-inline-block text-dark hover-t-decoration" title={novel.title} href="/#">{novel.title}</a>
            <div className="d-flex align-items-center view text-secondary">
                <i className="fa fa-eye"></i>
                <p>{novel.view}</p>
            </div>
        </td>
    </tr>
}
//---------------------------
const Sections = {ShowNovels, NovelsList, renderRankNovel};

export default Sections;
