import { useState, useEffect } from "react";

import Components from './../Components.js';
import { reviewNovels_, TopGoodNovels_, newUpdateNovels_ } from './../contentData_.js';
import lastUpdatedTime from "./../lastUpdatedTime.js";
import typeNovelsListData from '../DataNotUpdate/asideData.js';
import * as Common from './Common.js';

const Aside = () => {
    return <aside className="max-860-w-full col-4">
        <div className="m-content">
            <TypeNovelsList />
            <ReviewNovels />
            <NovelsList sectionName="TOP TRUYỆN HAY" novels={TopGoodNovels_} />
            <NovelsList sectionName="TRUYỆN MỚI ĐĂNG" novels={newUpdateNovels_} />
        </div>
    </aside>
}
export default Aside;

const TypeNovelsList = () => {
    return (
        <section className="bg-white type-novel">
            <Components.TitleSection title="THỂ LOẠI TRUYỆN" />
            <Components.TableOfList cssTable="table bg-white w-100" rows={typeNovelsListData} />
        </section>
    );
}

const ReviewNovels = () => {
    const sectionName = "REVIEW TRUYỆN";

    return (
        <section className="bg-white review-novel">
            <Components.TitleSection title={sectionName} />
            <Common.SimpleListOfNovels novels={reviewNovels_} nameUpdateTime="reviewNovels" />
            <Components.More title={sectionName} />
        </section>
    );
}

const NovelsList = props => {
    return <section className="bg-white">
        <Components.TitleSection title={props.sectionName} />
        <table className="table top-list"><tbody><RankList novels={props.novels} nameUpdateTime={props.nameUpdateTime} /></tbody></table>
        <Components.More title={props.sectionName} />
    </section>
}

const RankList = props => {
    let setUpdatedTime = useState(lastUpdatedTime[props.nameUpdateTime])[1];
    useEffect(() => {
        window.setLastUpdatedTime[props.nameUpdateTime] = setUpdatedTime;
    });

    return props.novels.map(novel =>
        <tr key={novel.title}>
            <td className={novel.cssRank}>{novel.rank}</td>
            <td>
                <a className="d-inline-block text-dark hover-t-decoration" title={novel.title} href="#0">{novel.title}</a>
                <div className="d-flex align-items-center view text-secondary">
                    <i className="fa fa-eye"></i>
                    <p>{novel.view}</p>
                </div>
            </td>
        </tr>
    );
}
