const Footer = () => {
    const novels = ["mẹ 17 tuổi con trai thiên tài cha phúc hắc", "thần ma hệ thống", "truyện trinh thám", "năm tháng vội vã", 
        "truyen sac dep kho cuong", "vô tận đan điền", "ngôn tình cung đấu", "bên nhau trọn đời", "huyền của ôn noãn", 
        "bach luyen thanh tien"
    ];
    const markers = [{title: "đọc truyện online", text: "đọc truyện"}, {title: "Truyện Tiên Hiệp", text: "truyện tiên hiệp"}, 
        {title: "Truyện Kiếm Hiệp", text: "truyện kiếm hiệp"}, {title: "Truyện Ngôn Tình", text: "truyện ngôn tình"}, 
        {title: "Truyện Teen", text: "truyện teen"}, {title: "Truyện Đô Thị", text: "truyện đô thị"}, 
        {title: "Privacy Policy", text: "Chính sách bảo mật"}, {title: "Terms", text: "Điều khoản sử dụng"}, 
        {title: "Content Rules", text: "Quy định về nội dung"}, {title: "DMCA Guidelines", text: "Vấn đề bản quyền"}, 
        {title: "Privacy agreement", text: "Thỏa thuận quyền riêng tư"}];

    return <footer class="footer">
    <div class="max-width-main mx-auto">
        <div class="bg-dark p-top-novel-month">
            <h2 class="text-white h2">Top truyện nổi bật của tháng</h2>
            {novels.map(renderNovel)}
        </div>

        <div class="d-flex flex-wrap pv-description">
            <div class="max-860-w-full col-8">
                <p class="text-white">Website&nbsp;<HighLigh data={markers[0]} />&nbsp;online chất lượng hàng đầu Việt Nam, với nhiều&nbsp;
                    <HighLigh data={markers[1]} />,&nbsp;<HighLigh data={markers[2]} />,&nbsp;<HighLigh data={markers[3]} />,&nbsp;
                    <HighLigh data={markers[4]} />,&nbsp;<HighLigh data={markers[5]} />&nbsp;được tác giả và dịch giả chọn lọc và đăng tải. 
                    Truy cập website nghĩa là bạn đã đồng ý với các quy định và điều khoản của chúng tôi. Vui lòng đọc kỹ các thông tin liên 
                    quan ở phía dưới.
                </p>
                <p class="text-white"><HighLigh data={markers[6]} />&nbsp;-&nbsp;<HighLigh data={markers[7]} />&nbsp;-&nbsp;
                <HighLigh data={markers[8]} />&nbsp;-&nbsp;<HighLigh data={markers[9]} />&nbsp;-&nbsp;<HighLigh data={markers[10]} />&nbsp;-
                &nbsp;
                
                <a class="text-link hover-t-decoration" href="" title="DMCA Guidelines">Vấn đề bản quyền</a> - 
                <a class="text-link hover-t-decoration" href="" title="Privacy agreement">Thỏa thuận quyền riêng tư</a></p>

                <p class="text-white">Website hoạt động dưới <a class="text-link hover-t-decoration" href="">Giấy phép truy cập mở Creative 
                Commons Attributio 4.0 International License.</a></p>

                <div class="d-flex align-items-center">
                    <a class="pr-logo" href="" title="DMCA.com Protection Program"><img src="images/dmca.png" alt=""/></a>
                    <a href="" title="Creative Commons License"><img src="images/ccBy.png" alt=""/></a>
                </div>
            </div>
                
            <div class="max-860-w-full col-4">
                <p class="text-white text-center">Copy right 2021 <a class="text-link" href="">DTruyen.Com</a></p>
            </div>
        </div>
    </div>
    </footer>
}

const renderNovel = (novel) => {
    return <a class="d-inline-block top-novel hover-t-decoration" href="">{novel}</a>
}

const HighLigh = (props) => {
    const data = props.data;

    return <a class="text-link hover-t-decoration" href="" title={data.title}>{data.text}</a>
}

export default Footer;
