import Sections from './Sections.js';
import { connect } from "react-redux";
import { useState, useEffect } from "react";

import MapState from './MapState.js';
import ConnectTemplate from './ConnectTemplate.js';
import Components from './Components.js';
import { carouselsData, SelectedTranslationNovels_, FullNovels_, UpdatedNovels_, leftOfShortNovels_, rightOfShortNovels_, reviewNovels_ } 
from './contentData.js';
import lastUpdatedTime from "./lastUpdatedTime.js";

const Content = () => {
    return (
        <div className="content">
            <div className="max-width-main mx-auto">
                <div className="d-flex flex-wrap mh-content-negative">
                    <Main />
                    <Aside />
                </div>
            </div>
        </div>
    );
}

const Main = () => {
    return (
        <main className="max-860-w-full col-8">
            <div className="m-content">
                <SuggestedNovels />
                <ShowNovels 
                sectionName="TRUYỆN DỊCH CHỌN LỌC" haveTypeNovels={0} novels={SelectedTranslationNovels_} 
                nameUpdateTime="selectedTranslationNovels" 
                />
                <UpdatedNovels />
                <ShowNovels sectionName="TRUYỆN FULL" haveTypeNovels={1} novels={FullNovels_} nameUpdateTime="fullNovels" />
                <ShortNovels />
            </div>
        </main>
    );
}

const SuggestedNovels = () => {
    const types = ["Ngôn Tình", "Truyện Teen", "Tiên Hiệp", "Kiếm Hiệp", "Xuyên Không"];

    return (
        <section className="bg-white">
            <Components.TitleSection title="TRUYỆN ĐỀ CỬ" />
            <Components.RenderTypeNovels types={types} />
            <div id="carouselExampleControls" className="carousel slide" data-bs-ride="carousel">
                <div className="carousel-inner"><Carousels /></div>
                <button 
                className="carousel-control-prev size-angle-icon" type="button" data-bs-target="#carouselExampleControls" 
                data-bs-slide="prev"
                >
                    <i className="fa fa-angle-left size-icon"></i>
                    <span className="visually-hidden">Previous</span>
                </button>
                <button className="carousel-control-next" type="button" data-bs-target="#carouselExampleControls" data-bs-slide="next">
                    <i className="fa fa-angle-right size-icon"></i>
                    <span className="visually-hidden">Next</span>
                </button>
            </div>
        </section>
    );
}

const Carousels = () => {
    let setUpdatedTime = useState(lastUpdatedTime.suggestedNovels)[1];
    useEffect(() => {
        window.setLastUpdatedTime.suggestedNovels = setUpdatedTime;
    });

    return carouselsData.map((suggestedNovels, i) =>
        <div key={i} className={suggestedNovels.cssCarousel}>
            <div className="d-grid grid-novel">
            {suggestedNovels.novels.map(novel =>
                <div key={novel.name} className="position-relative">
                    <img className="d-block" src={novel.image} alt="" title={novel.title} />
                    <div className="position-absolute start-0 end-0 bottom-0 bg-black-opacity p-above">
                        <a className="d-block text-white text-center hover-t-decoration" title={novel.title} href="#0">
                            <Components.NameNovel novel={novel} />
                        </a>
                        <p className="text-white text-center">{novel.chapter}</p>
                    </div>
                </div>
            )}
            </div>
        </div>
    );
}

const ShowNovels = props => {
    const types = ["Ngôn Tình", "Truyện Teen", "Tiên Hiệp", "Kiếm Hiệp", "Xuyên Không"];

    return (
        <section className="bg-white">
            <Components.TitleSection title={props.sectionName} />
            {(props.haveTypeNovels === 1) && <Components.RenderTypeNovels types={types} />}
            <div className="row cols-novel row-cols-4 m-0">
                <DecriptionNovels novels={props.novels} nameUpdateTime={props.nameUpdateTime} />
            </div>
            <Components.More title={props.sectionName} />
        </section>
    );
}

const DecriptionNovels = props => {
    let setUpdatedTime = useState(lastUpdatedTime[props.nameUpdateTime])[1];
    useEffect(() => {
        window.setLastUpdatedTime[props.nameUpdateTime] = setUpdatedTime;
    });

    return props.novels.map(novel =>
        <div key={novel.name} className="container-novel">
            <div><img src={novel.image} alt="" title={novel.title} /></div>
            <div className="container-infor-novel">
                <a className="d-block text-dark hover-t-decoration" title={novel.title} href="#0">
                    <Components.NameNovel novel={novel} />
                </a>
                <p className="text-secondary">{novel.chapter}</p>
            </div>
        </div>
    );
}

const UpdatedNovels = () => {
    const sectionName = "TRUYỆN MỚI CẬP NHẬT";
    const types = ["Ngôn Tình", "Truyện Teen", "Tiên Hiệp", "Kiếm Hiệp", "Xuyên Không"];

    return <section className="bg-white">
        <Components.TitleSection title={sectionName} />
        <Components.RenderTypeNovels types={types} />
        <table className="table table-hover table-update-novel align-middle"><tbody><UpdatedNovelsList /></tbody></table>
        <Components.More title={sectionName} />
    </section>
}

const UpdatedNovelsList = () => {
    let setUpdatedTime = useState(lastUpdatedTime.updatedNovels)[1];
    useEffect(() => {
        window.setLastUpdatedTime.updatedNovels = setUpdatedTime;
    });

    return UpdatedNovels_.map(novel => 
        <tr key={novel.name}>
            <td className="d-flex align-items-center">
                <a className="d-inline-block text-dark hover-t-decoration title-novel-update" title={novel.title} href="#0">
                    <Components.NameNovel novel={novel} />
                </a>
                {(novel.note !== "") && <p className="note text-hot size-text-secondary">{novel.note}</p>}
            </td>
            <td className="text-secondary td-secondary size-text-secondary">{novel.chapter}</td>
            <td className="text-secondary td-secondary size-text-secondary">{novel.time}</td>
        </tr>
    );
}

const ShortNovels = () => {
    return (
        <section className="bg-white">
            <Components.TitleSection title="TRUYỆN NGẮN" />
            <div className="d-flex flex-wrap col-short-novel row-cols-2 pb-shorts-novel">
                <LeftOfShortNovels />
                <SimpleListOfNovels novels={rightOfShortNovels_} nameUpdateTime="rightOfShortNovels" />
            </div>
        </section>
    );
}

const LeftOfShortNovels = () => {
    let setUpdatedTime = useState(lastUpdatedTime.leftOfShortNovel)[1];
    useEffect(() => {
        window.setLastUpdatedTime.leftOfShortNovel = setUpdatedTime;
    });

    return (
        <div className="position-relative">
            <img src={leftOfShortNovels_.image} alt="" title={leftOfShortNovels_.title} />
            <a 
            className="d-inline-block text-white hover-t-decoration position-absolute start-0 end-0 bottom-0 p-above" 
            title={leftOfShortNovels_.title} href="#0"
            >
                {leftOfShortNovels_.title}
            </a>
        </div>
    );
}

const SimpleListOfNovels = props => {
    let setUpdatedTime = useState(lastUpdatedTime[props.nameUpdateTime])[1];
    useEffect(() => {
        window.setLastUpdatedTime[props.nameUpdateTime] = setUpdatedTime;
    });

    return (
        <div className="right-novel">
        {props.novels.map(novel =>
            <div key={novel.title} className={novel.cssContainer}>
                <img src={novel.image} alt="" title={novel.title} />
                <a className="text-dark hover-t-decoration" title={novel.title} href="#0">{novel.title}</a>
            </div>
        )}
        </div>
    );
}

const Aside = () => {
    return <aside className="max-860-w-full col-4">
        <div className="m-content">
            <Sections.TypeNovels />
            <ReviewNovels />
            <Sections.NovelsList sectionName="TOP TRUYỆN HAY" RenderNovelsList={TopGoodNovelsHOC} />
            <Sections.NovelsList sectionName="TRUYỆN MỚI ĐĂNG" RenderNovelsList={NewUpdateNovelsHOC} />
        </div>
    </aside>
}

const ReviewNovels = () => {
    const sectionName = "REVIEW TRUYỆN";

    return (
        <section className="bg-white review-novel">
            <Components.TitleSection title={sectionName} />
            <SimpleListOfNovels novels={reviewNovels_} nameUpdateTime="reviewNovels" />
            <Components.More title={sectionName} />
        </section>
    );
}

const NovelsList = props => {
    return <section className="bg-white">
        <Components.TitleSection title={props.sectionName} />
        <RenderNovelsList />
        <Components.More title={props.sectionName} />
    </section>
}



const TopGoodNovelsHOC = connect(MapState.mapTopGoodNovels)(ConnectTemplate.RenderNovelsList);
const NewUpdateNovelsHOC = connect(MapState.mapNewUpdateNovels)(ConnectTemplate.RenderNovelsList);

export default Content;
