const TableOfList = props => {
    return <table className={props.cssTable}>
        {props.rows.map(ColumnsPerRow)}
    </table>
}

const ColumnsPerRow = (row, i) => {
    return <tr key={i}>{row.map(column => {return <RenderColumn column={column} />})}</tr>
}

const RenderColumn = props => {
    const column = props.column;

    return <td key={column.type} className={column.cssTd} colspan={column.colspan} title={column.title}>
        <a className={column.cssLink} href="">
            <i className={column.cssIcon}></i>
            <div><p className={column.cssType}>{column.type}</p></div>
        </a>
    </td>
}

const TitleSection = props => {
    return <h2><span className="hover-t-decoration" title={props.title}>{props.title}</span> <i class="fa fa-angle-right"></i></h2>
}

const RenderTypeNovels = props => {
    return <div className="d-flex flex-wrap container-type-novel">{props.types.map(renderTypeNovel)}</div>
}

const renderTypeNovel = type => {
    return <a className="d-block text-white hover-t-decoration" href="">{type}</a>
}

const titleNovel = novel => {
    const temp = (novel.before !== "") ? (novel.before + " " + novel.name) : novel.name;

    novel.title = (novel.after !== "") ? (temp + " " + novel.after) : temp;
}

const ContainerNovels = props => {
    return <div className="row cols-novel row-cols-4 m-0">{props.novels.map(VerticalDecription)}</div>
}

const VerticalDecription = novel => {
    titleNovel(novel);

    return <div className="container-novel">
        <div><img src={novel.image} alt="" title={novel.title} /></div>
        <div className="container-infor-novel">
            <a className="d-block text-dark hover-t-decoration" href="" title={novel.title}>
                <NameNovel novel={novel} />
            </a>
            <p className="text-secondary">{novel.chapter}</p>
        </div>
    </div>
}

const NameNovel = props => {
    const novel = props.novel;

    return <>{(novel.before !== "") && (novel.before + " ")}<cite>{novel.name}</cite>{(novel.after !== "") && (" " + novel.after)}</>
}

const More = props => {
    return <div className="d-flex">
        <a className="d-flex align-items-center justify-content-center text-white mx-auto pill" href="" title={props.title}>
            <p className="d-inline-block hover-t-decoration">XEM THÊM</p>
            <i className="d-inline-block fa fa-angle-down"></i>
        </a>
    </div>
}

const HorizontalDecription = novel => {
    return <div className={novel.cssContainer}>
        <img src={novel.image} alt="" title={novel.title} />
        <a className="text-dark hover-t-decoration" href="" title={novel.title}>{novel.title}</a>
    </div>
}

const Components = {TableOfList, TitleSection, RenderTypeNovels, titleNovel, ContainerNovels, NameNovel, More, HorizontalDecription};

export default Components;
