const propsTypeNovelList = typeNovel => {
    typeNovel.cssTd = "";
    typeNovel.colSpan = 1;
    typeNovel.title = typeNovel.type;
    typeNovel.cssLink = "d-flex align-items-center text-dark";
    typeNovel.cssIcon = "fa fa-tags";
    typeNovel.cssType = "d-inline-block pl-item hover-t-decoration";
}

export const typeNovelsListData = [
    [{type: "Tiên Hiệp"}, {type: "Kiếm Hiệp"}], [{type: "Truyện Teen"}, {type: "Ngôn Tình"}], [{type: "Đoản Văn"}, {type: "Đông Phương"}], 
    [{type: "Gia Đấu"}, {type: "Nữ Cường"}], [{type: "Cung Đấu"}, {type: "Truyện Sủng"}], [{type: "Truyện Ngược"}, {type: "Linh Dị"}], 
    [{type: "Thám Hiểm"}, {type: "Bách Hợp"}], [{type: "Hài Hước"}, {type: "Hiện Đại"}], [{type: "Việt Nam"}, {type: "Light Novel"}], 
    [{type: "Nữ Phụ"}, {type: "Phương Tây"}], [{type: "Mạt Thế"}, {type: "Cổ Đại"}], [{type: "Điền Văn"}, {type: "Đồng Nhân"}], 
    [{type: "Trọng Sinh"}, {type: "Dị Năng"}], [{type: "Huyền Huyễn"}, {type: "Dị Giới"}], [{type: "Võng Du"}, {type: "Quân Sự"}], 
    [{type: "Khoa Học"}, {type: "Lịch Sử"}], [{type: "Truyện Khác"}, {type: "Đô Thị"}], [{type: "Khoa Huyễn"}, {type: "Xuyên Không"}], 
    [{type: "Truyện Ma"}, {type: "Xuyên Nhanh"}], [{type: "Quan Trường"}, {type: "Đam Mỹ"}], [{type: "Hệ Thống"}, {type: "Tiểu Thuyết"}], 
    [{type: "Truyện Cười"}, {type: "Truyện Ngắn"}], [{type: "Truyện Trinh Thám"}, {type: "Truyện Sắc"}]
];
export default typeNovelsListData;

{
    let length = typeNovelsListData.length;

    for (let i = 0; i < length; i++) {
        typeNovelsListData[i].forEach(propsTypeNovelList);
    }

    for (let i = 0; i < 2; i++) {
        for (let j = 0; j < 2; j++) {
            typeNovelsListData[i][j].cssLink = "d-flex align-items-center text-hot";
            typeNovelsListData[i][j].cssIcon = "fa fa-diamond";
        }
    }
}
