const ItemNav = props => {return <div><p className="d-inline-block pl-item color-main-list item-main-hover">{props.item}</p></div>}
//---------------------------
const TableOfList = props => {return <table className={props.cssTable}>{props.rows.map(columnsPerRow)}</table>}

const columnsPerRow = (row, i) => {
    return <tr key={i}>{row.map(column => {return <RenderColumn key={column.type} column={column} />})}</tr>
}

const RenderColumn = ({column}) => {
    return <td className={column.cssTd} colSpan={column.colSpan} title={column.title}>
        <a className={column.cssLink} href="/#">
            <i className={column.cssIcon}></i>
            <div><p className={column.cssType}>{column.type}</p></div>
        </a>
    </td>
}
//---------------------------
const TitleSection = ({title}) => {
    return <h2><span className="hover-t-decoration" title={title}>{title}</span> <i className="fa fa-angle-right"></i></h2>
}
//---------------------------
const RenderTypeNovels = props => {
    return <div className="d-flex flex-wrap container-type-novel">{props.types.map(renderTypeNovel)}</div>
}

const renderTypeNovel = type => {
    return <a key={type} className="d-block text-white hover-t-decoration" href="/#">{type}</a>
}
//---------------------------
const titleNovel = ({title, before, name, after}) => {
    title = (before !== "") ? (before + " " + name) : name;
    title = (after !== "") ? (title + " " + after) : title;
}
//---------------------------
const NameNovel = ({novel}) => {
    return <>{(novel.before !== "") && (novel.before + " ")}<cite>{novel.name}</cite>{(novel.after !== "") && (" " + novel.after)}</>
}
//---------------------------
const More = props => {
    return <div className="d-flex">
        <a className="d-flex align-items-center justify-content-center text-white mx-auto pill" title={props.title} href="/#">
            <p className="d-inline-block hover-t-decoration">XEM THÃŠM</p>
            <i className="d-inline-block fa fa-angle-down"></i>
        </a>
    </div>
}
//---------------------------
const HorizontalDecription = novel => {
    return <div key={novel.title} className={novel.cssContainer}>
        <img src={novel.image} alt="" title={novel.title} />
        <a className="text-dark hover-t-decoration" title={novel.title} href="/#">{novel.title}</a>
    </div>
}
//---------------------------
const RenderNovelsList = ({novels}) => {
    rank(novels);
    classRank(novels);

    return <table className="table top-list">{novels.map(renderRankNovel)}</table>
}

const rank = novels => {
    let numberNovels = novels.length;
    let count = numberNovels.toString().length;

    for (let i = 0; i < numberNovels; i++)
    {
        novels[i].rank = (i + 1) + "";
        novels[i].rank = novels[i].rank.padStart(count, "0");
    }
}

const classRank = novels => {
    novels.map(novel => novel.cssRank = "rank text-under-r-3");
    novels[0].cssRank = "rank text-r-1";
    novels[1].cssRank = "rank text-r-2";
    novels[2].cssRank = "rank text-r-3";
}

const renderRankNovel = (novel) => {
    return <tr key={novel.title}>
        <td className={novel.cssRank}>{novel.rank}</td>
        <td>
            <a className="d-inline-block text-dark hover-t-decoration" title={novel.title} href="/#">{novel.title}</a>
            <div className="d-flex align-items-center view text-secondary">
                <i className="fa fa-eye"></i>
                <p>{novel.view}</p>
            </div>
        </td>
    </tr>
}
//---------------------------
const Components = {ItemNav, TableOfList, TitleSection, RenderTypeNovels, titleNovel, NameNovel, More, HorizontalDecription, 
    RenderNovelsList};

export default Components;
