import Components from './Components.js';
import * as headerData from "./dataNotUpdate.js";
import logo from "./images/logo.png";

const Header = () => {
    return <header className="header">
        <nav className="max-width-main mx-auto">
            <div className="d-flex flex-wrap mx-nav-negative">
                <ReadNovelOnline />
                {/* <ListOfTypeNovels /> */}
                <ItemsList cssIcon="d-block fa fa-tags color-main-list" item="Thể loại" cssTable={headerData.cssTable_} 
                rows={headerData.listOfTypeNovels} />
                {/* <Arrange /> */}
                <UpLoad />
                <Search />
                <LogIn />
            </div>
        </nav>
    </header>
}

const ReadNovelOnline = () => {
    return <div className="d-flex align-items-center p-item-main title-main-hover" title="doc truyen online">
        <img className="d-block img" src={logo} alt="" />
        <h1><a className="d-inline-block color-main-list" href="#0">Doc Tryen Online</a></h1>
    </div>
}

const ItemsList = props => {
    return (
        <div className="max-460-none d-flex-edit align-items-center p-item-main position-relative hover-show">
            <i className={props.cssIcon}></i>
            <Components.ItemNav item={props.item} />
            <Components.TableOfList cssTable={props.cssTable} rows={props.rows} />
        </div>
    );
}

