import Sections from './Sections.js';
import { connect } from "react-redux";
import MapState from './MapState.js';
import ConnectTemplate from './ConnectTemplate.js';
import Components from './Components.js';

const Content = () => {
    return <div className="content">
        <div className="max-width-main mx-auto">
            <div className="d-flex flex-wrap mh-content-negative">
                <Main />
                <Aside />
            </div>
        </div>
    </div>
}

const Main = () => {
    return <main className="max-860-w-full col-8">
        <div className="m-content">
            <SuggestedNovels />
            <Sections.ShowNovels sectionName="TRUYỆN DỊCH CHỌN LỌC" haveTypeNovels={0} VerticalDecriptionHOC={TranslationNovelsHOC} />
            <Sections.UpdatedNovels />
            <Sections.ShowNovels sectionName="TRUYỆN FULL" haveTypeNovels={1} VerticalDecriptionHOC={FullNovelsHOC} />
            <Sections.ShortNovels />
        </div>
    </main>
}

const SuggestedNovels = () => {
    const types = ["Ngôn Tình", "Truyện Teen", "Tiên Hiệp", "Kiếm Hiệp", "Xuyên Không"];

    return <section className="bg-white">
        <Components.TitleSection title="TRUYỆN ĐỀ CỬ" />
        <Components.RenderTypeNovels types={types} />
        <Carousels />
    </section>
}

const Carousels = () => {
    return <div id="carouselExampleControls" className="carousel slide" data-bs-ride="carousel">
        <div className="carousel-inner"><CarouselsHOC /></div>
        <button className="carousel-control-prev size-angle-icon" type="button" data-bs-target="#carouselExampleControls" data-bs-slide="prev">
            <i className="fa fa-angle-left size-icon"></i>
            <span className="visually-hidden">Previous</span>
        </button>
        <button className="carousel-control-next" type="button" data-bs-target="#carouselExampleControls" data-bs-slide="next">
            <i className="fa fa-angle-right size-icon"></i>
            <span className="visually-hidden">Next</span>
        </button>
    </div>
}

const CarouselsTemplate = ({SuggestedNovels}) => {
    const carouselsLength = Math.ceil(SuggestedNovels.length/3);
    const carousels = new Array(carouselsLength);

    for (let i = 0; i < carouselsLength; i++)
    {
        carousels[i] = {carousel: "carousel-item"};
        carousels[i].novels = [];
    }

    SuggestedNovels.map((novel, i) => carousels[Math.floor(i/3)].novels.push(novel));
    carousels[0].carousel = "carousel-item active";

    return carousels.map(renderSuggestedNovels);
}

const renderSuggestedNovels = (suggestedNovels, i) => {
    return <div key={i} className={suggestedNovels.carousel}>
        <div className="d-grid grid-novel">{suggestedNovels.novels.map(renderSuggestedNovel)}</div>
    </div>
}

const renderSuggestedNovel = novel => {
    Components.titleNovel(novel);

    return <div key={novel.name} className="position-relative">
        <img className="d-block" src={novel.image} alt="" title={novel.title} />
        <div className="position-absolute start-0 end-0 bottom-0 bg-black-opacity p-above">
            <a className="d-block text-white text-center hover-t-decoration" title={novel.title} href="#0">
                <Components.NameNovel novel={novel} />
            </a>
            <p className="text-white text-center">{novel.chapter}</p>
        </div>
    </div>
}

const TranslationNovelsHOC = connect(MapState.mapSelectedTranslationNovels)(ConnectTemplate.VerticalDecription);
const FullNovelsHOC = connect(MapState.mapFullNovels)(ConnectTemplate.VerticalDecription);

const Aside = () => {
    return <aside className="max-860-w-full col-4">
        <div className="m-content">
            <Sections.TypeNovels />
            <Sections.ReviewNovels />
            <Sections.NovelsList sectionName="TOP TRUYỆN HAY" RenderNovelsList={TopGoodNovelsHOC} />
            <Sections.NovelsList sectionName="TRUYỆN MỚI ĐĂNG" RenderNovelsList={NewUpdateNovelsHOC} />
        </div>
    </aside>
}

const TopGoodNovelsHOC = connect(MapState.mapTopGoodNovels)(ConnectTemplate.RenderNovelsList);
const NewUpdateNovelsHOC = connect(MapState.mapNewUpdateNovels)(ConnectTemplate.RenderNovelsList);

export default Content;
